mapper.select:
SELECT details_id, quotation_id, equipment_num, equipment_name, (SELECT material_name FROM material_supplier ms WHERE ms.supplier_id = bqd.product_supplier_id ) AS productSupplierName, (SELECT bol.outsourcing_line FROM busi_outsourcing_line bol WHERE bol.outsourcing_line_id = bqd.product_line_id ) AS productLineName, (SELECT bo.outsourcing_model FROM busi_outsourcing bo WHERE bo.outsourcing_id = bqd.model_id ) AS modelName, number, rinse_solution_id, other_expenses, other_expenses_description, rated_flow, lift, operating_temperature, fluid_density, inlet_pressure, npsh, motor_supplier_id, (SELECT material_name FROM material_supplier ms WHERE ms.supplier_id = bqd.motor_supplier_id ) AS motor_supplier_name, motor_id, (SELECT motor_model FROM busi_motor bu WHERE bu.motor_id = bqd.motor_id) AS motor_name, other_motor, other_motor_price,
quotation_type, coefficient, details_price FROM busi_outsourcing_details bqd
and bqd.quotation_id = #{quotationId}

mapper.update:
update busi_outsourcing_details
quotation_id = #{quotationId}, equipment_num = #{equipmentNum}, equipment_name = #{equipmentName}, product_line_id = #{productLineId}, model_id = #{modelId}, product_supplier_id = #{productSupplierId}, number = #{number}, rinse_solution_id = #{rinseSolutionId}, other_expenses = #{otherExpenses}, other_expenses_description = #{otherExpensesDescription}, rated_flow = #{ratedFlow}, lift = #{lift}, operating_temperature = #{operatingTemperature}, fluid_density = #{fluidDensity}, inlet_pressure = #{inletPressure}, npsh = #{npsh}, motor_supplier_id = #{motorSupplierId}, motor_id = #{motorId}, other_motor = #{otherMotor}, other_motor_price = #{otherMotorPrice}, quotation_type = #{quotationType}, coefficient = #{coefficient}, details_price = #{detailsPrice},
where details_id = #{detailsId}
